'use strict';
import { fx, range } from './src/index';

const multiplyBy = n => function*(iterator) {
  for (let value of iterator) {
    yield n * value;
  }
};

const negate = function* (iterator) {
  for (let value of iterator) {
    yield -value;
  }
};

const log = function* (iterator) {
  for (let value of iterator) {
    console.log('>', value);
    yield value;
  }
  console.log('---');
};


for (let i of fx([1, 2, 3, 4]).filter(x => x > 2)) {
  console.log(i);
}

console.log(
  ...range()
    .filter(x => x > 1)
    .map(x => x * 2)
    .take(20)
    .compose(
      negate,
      negate
    )
    .chunk(2)
    .apply(log)
    .zip()
);
